import { BrowserRouter, Route, Routes } from 'react-router-dom';
import './App.css';
import NavBar from './NavBar';
import { useState, useEffect } from 'react';
import SearchBook from './SearchBook';
import dataSource from './dataSource';
import EditBook from  './EditBook';

const App = () => {

  const[bookList, setBookList] = useState([]);
  const [searchPhrase, setSearchPhrase] = useState('');

  const [currentSelectedBookId, setCurrentSelectedBookId] = useState(0);

  const loadBooks = async () => {
    const response = await dataSource.get(`/books`);
    setBookList(response.data);
  }

  let refresh = false;

  useEffect(() =>{
    loadBooks();
  }, [refresh]
  );

  const updateSearchResults  = async (phrase) =>{
    console.log('Phrase is = ' + phrase);
    setSearchPhrase(phrase);
}


  const renderedList = bookList.filter((book) =>{

    if(
        book.title.toLowerCase().includes(searchPhrase.toLowerCase()) ||
        searchPhrase === ''
    ){
        return true;
    }
    return false;
});

  return (
    <BrowserRouter>
      <NavBar/>
      <Routes>
        <Route
          exact
          path ='/'
          element={
            <SearchBook
              updateSearchResults={updateSearchResults}
              bookList ={renderedList}
            />
          }
        />
        <Route exact path='/new' element={<EditBook onEditAlbum={onEditBook}/>} />
        <Route exact path='/edit/:albumID' element={<EditBook onEditAlbum={onEditBook} book={bookList[currentSelectedBookId]}/>} />
        <Route
              exact
              path='/show/:albumId'
              element={<OneBook book={bookList[currentSelectedBookId]} /> }
       />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
