import React from "react";

const Card = (props) =>{

    const handleButtonClick = (event, uri) =>{
        props.onClick(props.bookId, uri);
    }

    const imgUrl = 'https://www.nicepng.com/png/detail/121-1212604_big-image-book-clipart-clip-art-books.png'



    return (
        <div className="card" style={{width: '18rem'}}>
        <img src={imgUrl} className="card-img-top" alt="..." />
        <div className="card-body">
            <h5 className="card-title">{props.bookTitle}</h5>
            <h3 className="card-text">
                {props.bookAuthor}
            </h3>
            <button 
                onClick={() => handleButtonClick(props.bookId,'/show/')}
                className="btn btn-primary"
            >
                {props.buttonText}
            </button>
            <button 
                onClick={() => handleButtonClick(props.bookId,'/edit/')}
                className="btn btn-primary"
            >
                Edit
            </button>
        </div>      
    </div>
    )
}

export default Card;
